def count_xmas_in_grid(input, word="XMAS"):
    grid = input.splitlines()  # Split input string into rows
    rows = len(grid)
    cols = max(len(row) for row in grid)  # Handle rows of varying lengths
    word_length = len(word)
    directions = [
        (0, 1),  # Right
        (1, 0),  # Down
        (0, -1), # Left
        (-1, 0), # Up
        (1, 1),  # Down-Right
        (1, -1), # Down-Left
        (-1, 1), # Up-Right
        (-1, -1) # Up-Left
    ]

    def is_valid(x, y):
        return 0 <= x < rows and 0 <= y < len(grid[x])

    def find_word(start_x, start_y, dx, dy):
        for k in range(word_length):
            nx, ny = start_x + k * dx, start_y + k * dy
            if not is_valid(nx, ny) or grid[nx][ny] != word[k]:
                return False
        return True

    count = 0
    for x in range(rows):
        for y in range(len(grid[x])):  # Iterate only through valid columns in each row
            for dx, dy in directions:
                if find_word(x, y, dx, dy):
                    count += 1

    return count

