def count_xmas_in_grid(input):
    grid = input.splitlines()  # Split input string into rows
    rows = len(grid)
    cols = max(len(row) for row in grid)  # Handle rows of varying lengths
    count = 0

    # Iterate over all possible centers for the X (i.e., 'A' position)
    for i in range(1, rows-1):
        for j in range(1, cols-1):
            # Check the condition where opposite corners have opposite letters (one 'S' and one 'M')
            if (grid[i-1][j-1] in ['S', 'M'] and grid[i+1][j+1] in ['S', 'M'] and 
                grid[i-1][j+1] in ['S', 'M'] and grid[i+1][j-1] in ['S', 'M'] and 
                grid[i-1][j-1] != grid[i+1][j+1] and grid[i-1][j+1] != grid[i+1][j-1] and 
                grid[i][j] == 'A'):
                count += 1

    return count

